import { useEffect } from "react";
import { createContext } from "react";
import { useState } from "react";


export const AccountContext = createContext();

// Keep track of whether logged in or not
const UserContext = ({children}) => {
    const [user, setUser] = useState({ loggedIn: null });
    useEffect(() => {}, []); // Function that runs when array changes and when component mounts which will be when the page is refreshed
    return <AccountContext.Provider value={{user, setUser}}>
        {children}
    </AccountContext.Provider>
}

export default UserContext;
